mapper.select:
select
  hotel_id, hotel_name, hotel_phone, hotel_site, hotel_class
from hotel


        and hotel_id = #{hotelId}


        and hotel_name = #{hotelName}


        and hotel_phone = #{hotelPhone}


        and hotel_site = #{hotelSite}


        and hotel_class = #{hotelClass}


order by hotel_id desc
limit #{page}, #{limit}

select count(1)
from hotel


        and hotel_id = #{hotelId}


        and hotel_name = #{hotelName}


        and hotel_phone = #{hotelPhone}


        and hotel_site = #{hotelSite}


        and hotel_class = #{hotelClass}
