mapper.select:
select
  p_id, p_name, p_power, p_phone, password, birth
from person


        and p_id = #{pId}


        and p_name = #{pName}


        and p_power = #{pPower}


        and p_phone = #{pPhone}


        and password = #{password}


        and birth = #{birth}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from person


        and p_id = #{pId}


        and p_name = #{pName}


        and p_power = #{pPower}


        and p_phone = #{pPhone}


        and password = #{password}


        and birth = #{birth}
